import {Token} from "../Main"
import {Box, makeStyles} from "@material-ui/core"
import {TabContext, TabList, TabPanel} from "@material-ui/lab"
import React, {useState} from "react"
import {Tab} from "@material-ui/core"
import {WalletBalance} from "./WalletBalance"
import {StakeForm} from "./StakeForm" 

interface YourWalletProps{
    supportTokens: Array<Token>
}

const useStyles = makeStyles((theme) => ({
    tabContent: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: theme.spacing(4)
    },
    box: {
        backgroundColor: "white",
        borderRadius: "25px"
    },
    header: {
        color: "white"
    }
}))
export const YourWallet = ({supportTokens} : YourWalletProps) => {
    const [selectedTokenIndex, setSelectedTokenIndex] = useState<number>(0)
    const handleChange = (event: React.ChangeEvent<{}>, newValue: string) => {
        setSelectedTokenIndex(parseInt(newValue))
    }
    const classes = useStyles()

    return (    
        <Box>
            <h1>Your Wallet</h1>
            <Box>
                <TabContext value={selectedTokenIndex.toString()}>
                    <TabList onChange={handleChange} aria-label="stake form tabs">
                        {supportTokens.map((token, index) => {
                            return (
                                <Tab label={token.name} value={index.toString()}  key={index} />
                            )
                        })}
                    </TabList>
                    {supportTokens.map((token, index) => {
                        return (
                            <TabPanel value={index.toString()} key={index}>
                                <div className={classes.tabContent}>
                                <WalletBalance token={supportTokens[selectedTokenIndex]} />
                                <StakeForm token={supportTokens[selectedTokenIndex]} />
                                </div>
                            </TabPanel>
                        )
                    })}
                </TabContext>
            </Box>
        </Box>
    )
}
